        if (PositivoCovid == null || PositivoCovid == "default")
        {
            ok = false;
            error = error + "\nSeleccione si le ha dado covid o no.";
        }
        if (vacunado.Justificacion == null || vacunado.Justificacion == "")
        {
            ok = false;
            error = error + "\nLa justificacion es obligatoria.";
        }

        if (ok)
        {
            todoCorrecto();
        }

    }
